mapper.delete:
delete from like_info
where u_email=#{u_email}
and post_seq=#{post_seq}

mapper.update:
update post_info
set likes = (select count(*)
				from like_info
				where post_seq=#{post_seq}
			)
where post_seq=#{post_seq}

mapper.select:
select * from like_info 
where u_email = #{u_email}
and post_seq = #{post_seq}
